Alias.script:
-- echo("<UMLAUTE herausgenommen> <"..command..">\n");
command = string.gsub(command, 'ü', 'ue' );
command = string.gsub(command, 'ö', 'oe' );
command = string.gsub(command, 'ä', 'ae' );
command = string.gsub(command, 'Ä', 'Ae' );
command = string.gsub(command, 'Ö', 'Oe' );
command = string.gsub(command, 'Ü', 'Ue' );
command = string.gsub(command, 'ß', 'ss' );

send(command);

Script.script:
function debugText(text)
    echo("DEBUG: " .. text)
end

function debugDisplay(obj)
    echo("DEBUG: ")
    display(obj)
end

function zeigeEbenen()
  fg(farben.vg.ebenen)
  bg(farben.hg.ebenen)
  echo(gmcp.comm.channel.msg)
  resetFormat()
end

-- Entfernt Mudlets generic mapper package
uninstallPackage("generic_mapper")

mudlet = mudlet or {}
mudlet.mapper_script = true

mapper = {}

mapper.mode = "fix"
mapper.currentHash = nil
mapper.currentArea = "world"

function echoM(str)
    fg(mapperconf.color)
    echo("[MAPPER] " .. str .. "\n")
    resetFormat()
end

mapperconf = {}
------------------------------------------------------- -- Benutzerdefinierte Einstellungen fuer den Mapper. -- -------------------------------------------------------
-- Farbe fuer Mitteilungen des Mappers mapperconf.color = "royal_blue" -- Standardabstand zwischen zwei Raeumen mapperconf.scale = 5
