Trigger.script:
local zustand = {}
zustand["ist absolut fit."] = 1 zustand["ist leicht geschwaecht."] = 0.9 -- Kaempfer Fokus zustand["ist schon etwas geschwaecht."] = 0.9 -- untersuche zustand["fuehlte sich auch schon besser."] = 0.8 -- Kaempfer Fokus zustand["fuehlte sich heute schon besser."] = 0.8 -- untersuche zustand["ist leicht angekratzt."] = 0.7 -- Kaempfer Fokus zustand["ist leicht angeschlagen."] = 0.7 -- untersuche zustand["ist nicht mehr taufrisch."] = 0.6 -- Kaempfer Fokus zustand["sieht nicht mehr taufrisch aus."] = 0.6 -- untersuche zustand["sieht recht mitgenommen aus."] = 0.5 -- Kaempfer Fokus zustand["macht einen mitgenommenen Eindruck."] = 0.5 -- untersuche zustand["wankt bereits bedenklich."] = 0.4 zustand["ist in keiner guten Verfassung."] = 0.3 zustand["braucht dringend einen Arzt."] = 0.2 zustand["steht auf der Schwelle des Todes."]= 0.1
selectCurrentLine() fg(farben.vg.info) bg(farben.hg.info)
local ausgabe = zustand[matches[1]]
if ausgabe then ausgabe = " (" .. ausgabe*100 .. "%)" else ausgabe = " (???%)" end
echo(ausgabe)

Alias.script:
-- echo("<UMLAUTE herausgenommen> <"..command..">\n");
command = string.gsub(command, 'ü', 'ue' );
command = string.gsub(command, 'ö', 'oe' );
command = string.gsub(command, 'ä', 'ae' );
command = string.gsub(command, 'Ä', 'Ae' );
command = string.gsub(command, 'Ö', 'Oe' );
command = string.gsub(command, 'Ü', 'Ue' );
command = string.gsub(command, 'ß', 'ss' );

send(command);

Script.script:
function debugText(text)
	echo("DEBUG: " .. text)
end

function debugDisplay(obj)
	echo("DEBUG: ")
	display(obj)
end
