Script.script:
mapperconf = {}
------------------------------------------------------- -- Benutzerdefinierte Einstellungen fuer den Mapper. -- -------------------------------------------------------
-- Farbe fuer Mitteilungen des Mappers mapperconf.color = "blue"

mudlet = mudlet or {}
mudlet.mapper_script = true

mapper = {}

mapper.mode = "fix"
mapper.currentHash = nil
mapper.currentArea = "world"

function echoM(str)
    fg(mapperconf.color)
    echo("[MAPPER] " .. str .. "\n")
    resetFormat()
end
